// Normalize a field if it contains "missing sample data"
function normalizeField(fieldValue) {
  if (typeof fieldValue === "string" && fieldValue.trim().toLowerCase() === "missing sample data") {
    return "";
  }
  return fieldValue || "";
}

// Input data
let fullName = normalizeField(inputData.fullName);
let givenFirst = normalizeField(inputData.contactInformationFirstName);
let givenLast = normalizeField(inputData.contactInformationLastName);

// Default to the given first/last if they exist
let firstName = givenFirst;
let lastName = givenLast;

// Only run the name splitting if both are empty
if (!givenFirst && !givenLast) {
  const nameParts = fullName.trim().split(/\s+/);

  if (nameParts.length === 1) {
    firstName = nameParts[0];
    lastName = "";
  } else {
    firstName = nameParts[0];
    lastName = nameParts.slice(1).join(" ");
  }
}

return {
  "Standardized First Name": firstName,
  "Standardized Last Name": lastName
};

const employeesInput = inputData.contactInformationNumberOfEmployees;

// Convert to string to ensure we can use string methods
let employeesStr = (employeesInput || "").toString().trim();

// Replace " to " with a dash for uniform parsing
if (employeesStr.includes(" to ")) {
  employeesStr = employeesStr.replace(" to ", "-");
}

// If blank, "missing sample data", or no dash, return blank
if (!employeesStr || employeesStr === "missing sample data" || !employeesStr.includes('-')) {
  return { "Standardized Employees Range": "" };
}

const [minStr] = employeesStr.split('-');
const min = Number(minStr);

// If not a number, return blank
if (isNaN(min)) {
  return { "Standardized Employees Range": "" };
}

let outputRange = "";
if (min >= 1 && min <= 5) {
  outputRange = "1-5";
} else if (min > 5 && min <= 25) {
  outputRange = "5-25";
} else if (min > 25 && min <= 50) {
  outputRange = "25-50";
} else if (min > 50 && min <= 100) {
  outputRange = "50-100";
} else if (min > 100 && min <= 500) {
  outputRange = "100-500";
} else if (min > 500 && min <= 1000) {
  outputRange = "500-1000";
} else if (min > 1000) {
  outputRange = "1000+";
} else {
  outputRange = "";
}

return { "Standardized Employees Range": outputRange };
